word() {    return $this->user_password;  }  public function user_hint() {    return $this->user_hint;  }  public function user_type() {    return $this->user_type;  }  public function user_value() {    return $this->user_value;  }  public function user_role_id() {    return $this->user_role_id;  }  public function user_session_id() {    return $this->user_session_id;  }  public function getRole()  {    return $this->user_role_id;  }  public function getType()  {    return $this->user_type;  }  public function getTypeValue()  {    return $this->user_value;  }    public function userEmail() {    return $this->userEmail;  }}